mapper.select:
SELECT e.*, s.name as student_name, s.snum as student_snum, i.name as industry_name FROM employment_info e LEFT JOIN students s ON e.student_id = s.id LEFT JOIN industries i ON e.industry_id = i.id
AND (s.name LIKE CONCAT('%', #{keyword}, '%') OR e.company LIKE CONCAT('%', #{keyword}, '%') OR i.name LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY e.id LIMIT #{offset}, #{limit}

mapper.update:
UPDATE employment_info
SET student_id = #{studentId}, company = #{company}, position = #{position}, 
    salary = #{salary}, employment_date = #{employmentDate}, industry_id = #{industryId}, province = #{province}
WHERE id = #{id}
